mapper.select:
		SELECT COUNT(1) AS NCNT 
		  FROM EPGM_EXCA_LST 
        WHERE EXCA_STD_CD = #{EXCA_STD_CD_SEL}
            AND EXCA_PROC_STAT_CD != 'I' /* 발급 */
            AND MFC_BIZRID = #{MFC_BIZRID}
            AND MFC_BIZRNO = #{MFC_BIZRNO}

mapper.update:

DECLARE 
    BEGIN
        /* 출고정정 */  
        UPDATE EXCA_DLIVY_CRCT A 
               SET DLIVY_CRCT_STAT_CD = 'C' /* 정정확인 */,
               STAC_DOC_NO = '' ,
               UPD_PRSN_ID = #{S_USER_ID} ,
               UPD_DTTM = SYSDATE 
         WHERE 1=1 
               AND A.DLIVY_CRCT_STAT_CD = 'I' /* 정산 */ 
               AND EXISTS 
               (SELECT 'X' 
                 FROM EPGM_EXCA_LST B 
                WHERE 1=1 
                      AND B.EXCA_STD_CD = #{EXCA_STD_CD_SEL} 
                      AND B.EXCA_PROC_STAT_CD = 'I' /* 발급 */ 
                      AND A.STAC_DOC_NO = B.STAC_DOC_NO 
                      AND B.MFC_BIZRID = #{MFC_BIZRID} 
                      AND B.MFC_BIZRNO = #{MFC_BIZRNO} 
               ) 
        ;  

        /* 입고정정 */
        UPDATE EXCA_WRHS_CRCT A 
               SET WRHS_CRCT_STAT_CD = 'C' /* 정정확인 */ ,
               MFC_STAC_DOC_NO = '' ,
               WHSDL_STAC_DOC_NO = '' ,
               UPD_PRSN_ID = #{S_USER_ID} ,
               UPD_DTTM = SYSDATE 
         WHERE 1=1 
               AND WRHS_CRCT_STAT_CD IN ('I','E') /* 생산자/도매업자 정산 */ 
               AND EXISTS 
               (SELECT 'X' 
                 FROM EPGM_EXCA_LST B 
                WHERE 1=1 
                      AND B.EXCA_STD_CD = #{EXCA_STD_CD_SEL} 
                      AND B.EXCA_PROC_STAT_CD = 'I' /* 발급 */ 
                      AND 
                      (
                          A.MFC_STAC_DOC_NO = B.STAC_DOC_NO 
                          OR A.WHSDL_STAC_DOC_NO = B.STAC_DOC_NO
                      ) 
                      AND B.MFC_BIZRID = #{MFC_BIZRID} 
                      AND B.MFC_BIZRNO = #{MFC_BIZRNO} 
               ) 
        ;  

        /* 직접회수정정 */
        UPDATE EXCA_DRCT_RTRVL_CRCT A 
               SET DRCT_RTRVL_CRCT_STAT_CD = 'C' /* 정정확인 */ ,
               STAC_DOC_NO = '' ,
               UPD_PRSN_ID = #{S_USER_ID} ,
               UPD_DTTM = SYSDATE 
         WHERE 1=1 
               AND A.DRCT_RTRVL_CRCT_STAT_CD = 'I' /* 정산 */ 
               AND EXISTS 
               (SELECT 'X' 
                 FROM EPGM_EXCA_LST B 
                WHERE 1=1 
                      AND B.EXCA_STD_CD = #{EXCA_STD_CD_SEL} 
                      AND B.EXCA_PROC_STAT_CD = 'I' 
                      /* 발급 */ 
                      AND A.STAC_DOC_NO = B.STAC_DOC_NO 
                      AND B.MFC_BIZRID = #{MFC_BIZRID} 
                      AND B.MFC_BIZRNO = #{MFC_BIZRNO} 
               ) 
        ;  

        /* 정산연간조정 */
        UPDATE EXCA_FYER_CRCT A 
               SET ADJ_PROC_STAT = 'R' /*등록*/ ,
               STAC_DOC_NO = '' ,
               UPD_PRSN_ID = #{S_USER_ID} ,
               UPD_DTTM = SYSDATE 
         WHERE 1=1 
               AND A.ADJ_PROC_STAT = 'I' /* 정산 */ 
               AND EXISTS 
               (SELECT 'X' 
                 FROM EPGM_EXCA_LST B 
                WHERE 1=1 
                      AND B.EXCA_STD_CD = #{EXCA_STD_CD_SEL} 
                      AND B.EXCA_PROC_STAT_CD = 'I' /* 발급 */ 
                      AND A.STAC_DOC_NO = B.STAC_DOC_NO 
                      AND B.MFC_BIZRID = #{MFC_BIZRID} 
                      AND B.MFC_BIZRNO = #{MFC_BIZRNO} 
               ) 
        ;  

        /* 정산연간조정 */
        UPDATE EXCA_FYER_CRCT A 
               SET ADJ_PROC_STAT2 = 'R' /*등록*/ ,
               STAC_DOC_NO2 = '' ,
               UPD_PRSN_ID = #{S_USER_ID} ,
               UPD_DTTM = SYSDATE 
         WHERE 1=1 
               AND A.ADJ_PROC_STAT2 = 'I' /* 정산 */ 
               AND EXISTS 
               (SELECT 'X' 
                 FROM EPGM_EXCA_LST B 
                WHERE 1=1 
                      AND B.EXCA_STD_CD = #{EXCA_STD_CD_SEL} 
                      AND B.EXCA_PROC_STAT_CD = 'I' /* 발급 */ 
                      AND A.STAC_DOC_NO2 = B.STAC_DOC_NO 
                      AND B.MFC_BIZRID = #{MFC_BIZRID} 
                      AND B.MFC_BIZRNO = #{MFC_BIZRNO} 
               ) 
        ;

        /* 생산자 보증금잔액 */
        UPDATE EPGM_MFC_GTN_BAL A 
               SET DEL_YN = 'Y' ,
               UPD_PRSN_ID = #{S_USER_ID} ,
               UPD_DTTM = SYSDATE 
         WHERE 1=1 
               AND EXISTS 
               (SELECT 'X' 
                 FROM EPGM_EXCA_LST B 
                WHERE 1=1 
                      AND B.EXCA_STD_CD = #{EXCA_STD_CD_SEL} 
                      AND B.EXCA_PROC_STAT_CD = 'I' 
                      /* 발급 */ 
                      AND A.MAPP_DOC_NO = B.STAC_DOC_NO 
                      AND B.MFC_BIZRID = #{MFC_BIZRID} 
                      AND B.MFC_BIZRNO = #{MFC_BIZRNO} 
               ) 
        ;  

        /* 입고정정 연도변경 */
        UPDATE EXCA_WRHS_CRCT A 
               SET WRHS_CRCT_STAT_CD = 'C' /* 정정확인 */ ,
               MFC_STAC_DOC_NO = '' ,
               WHSDL_STAC_DOC_NO = '' ,
               UPD_PRSN_ID = #{S_USER_ID} ,
               UPD_DTTM = SYSDATE 
         WHERE 1=1 
               AND WRHS_CRCT_STAT_CD IN ('I','E') /* 생산자/도매업자 정산 */ 
               AND EXISTS 
               (SELECT 'X' 
                 FROM EPGM_EXCA_DTL B, 
                      EPGM_EXCA_LST C 
                WHERE 1=1 
                      AND B.MAPP_DOC_NO = A.WRHS_CRCT_DOC_NO 
                      AND B.STAC_DOC_NO = C.STAC_DOC_NO 
                      AND 'G' = C.EXCA_ISSU_SE_CD 
                      AND 'Y' = C.YEAR_CHG_YN 
                      AND C.EXCA_STD_CD = #{EXCA_STD_CD_SEL} 
                      AND C.EXCA_PROC_STAT_CD = 'I' /* 발급 */ 
                      AND C.MFC_BIZRID = #{MFC_BIZRID} 
                      AND C.MFC_BIZRNO = #{MFC_BIZRNO} 
               ) ; 
    END;

mapper.delete:

		DECLARE
	        BEGIN

	        		DELETE 
	        		  FROM EPGM_EXCA_DTL A
	        		WHERE 1=1
	        		    AND EXISTS (
	        		    					SELECT 'X'
	        		    					  FROM EPGM_EXCA_LST B
	        		    					WHERE 1=1
	        		    					    AND B.EXCA_STD_CD = #{EXCA_STD_CD_SEL}
			                                    AND B.EXCA_PROC_STAT_CD = 'I' /* 발급 */
			                                    AND A.STAC_DOC_NO = B.STAC_DOC_NO
			                                    AND B.MFC_BIZRID = #{MFC_BIZRID}
            									AND B.MFC_BIZRNO = #{MFC_BIZRNO}
	        		    				)
	        		;

	        		DELETE 
	        		   FROM EPGM_EXCA_LST 
	        		WHERE EXCA_STD_CD = #{EXCA_STD_CD_SEL} 
	        		    AND EXCA_PROC_STAT_CD = 'I' /* 발급 */
	        		    AND MFC_BIZRID = #{MFC_BIZRID}
            			AND MFC_BIZRNO = #{MFC_BIZRNO}
	        		;

		      END;

mapper.update:
 UPDATE EPGM_EXCA_LST
	   SET EXCA_AMT = (EXCA_AMT - NVL(ADD_AMT,0)) + #{ADD_AMT}
	         ,RISU_SE = 'R' /* 재고지 */
	         ,RISU_RSN = #{RISU_RSN}
	         ,RISU_DT = TO_CHAR(SYSDATE, 'YYYYMMDD')
	         ,ADD_AMT = #{ADD_AMT}
	         ,ADD_AMT_SE = #{ADD_AMT_SE}
	         ,UPD_PRSN_ID = #{S_USER_ID}
             ,UPD_DTTM = SYSDATE
  WHERE 1=1
	  AND STAC_DOC_NO = #{STAC_DOC_NO}
	  AND EXCA_ISSU_SE_CD = #{EXCA_ISSU_SE_CD}
	  AND EXCA_PROC_STAT_CD = 'I' /* 발급 */

mapper.delete:
DECLARE BEGIN
DELETE FROM EPGM_EXCA_DTL_MNUL A WHERE 1=1 AND EXISTS ( SELECT 'X' FROM EPGM_EXCA_LST B WHERE 1=1 AND B.STAC_DOC_NO = #{STAC_DOC_NO} AND B.EXCA_PROC_STAT_CD = 'I' /* 발급 */ AND A.STAC_DOC_NO = B.STAC_DOC_NO ) ;
DELETE FROM EPGM_EXCA_LST WHERE 1=1 AND STAC_DOC_NO = #{STAC_DOC_NO} AND EXCA_PROC_STAT_CD = 'I' /* 발급 */ ; END;
